import React, { useState, useEffect } from "react";
import axios from "axios";
import styled from "styled-components";

const Container = styled.div`
  font-family: "SF Pro", "Helvetica", "Myriad Set", sans-serif;
  max-width: 800px;
  margin: auto;
  padding: 20px;
  color: #333;
`;

const Heading = styled.h2`
  font-size: 2rem;
  font-weight: bold;
  color: #007aff; /* Apple Blue */
  text-align: center;
  margin-bottom: 20px;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  font-size: 1rem;
  border: 2px solid #ccc;
  border-radius: 8px;
  margin-bottom: 10px;
`;

const Button = styled.button`
  background-color: #007aff;
  color: white;
  border: none;
  padding: 10px;
  font-size: 1rem;
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.3s;
  width: 100%;
  
  &:hover {
    background-color: #005ecb;
  }
`;

const List = styled.ul`
  list-style: none;
  padding: 0;
`;

const ListItem = styled.li`
  background: #f9f9f9;
  padding: 10px;
  margin: 5px 0;
  border-radius: 8px;
  word-wrap: break-word; /* ✅ Wraps long words */
  overflow-wrap: break-word; /* ✅ Ensures text doesn't overflow */
`;

const TfidfContainer = styled.div`
  background: #f3f3f3;
  padding: 10px;
  border-radius: 8px;
  font-size: 0.9rem;
  word-wrap: break-word;
  overflow-wrap: break-word;
  white-space: pre-wrap; /* ✅ Ensures JSON is formatted nicely */
`;

function CompetitorAnalysis() {
  const [username, setUsername] = useState("");
  const [channelData, setChannelData] = useState(null);
  const [videos, setVideos] = useState([]);
  const [keywordAnalysis, setKeywordAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch videos and keyword analysis
  const fetchVideos = async () => {
    setLoading(true);
    setError(null);
    
    try {
      const response = await axios.post("http://localhost:5000/analyze", { username });

      console.log("API Response:", response.data); // Debugging

      setChannelData(response.data.channelData);
      setVideos(response.data.videos);
      setKeywordAnalysis(response.data.keywordAnalysis);

    } catch (err) {
      console.error("Error fetching data:", err);
      setError("Failed to fetch data. Check username and API.");
    }

    setLoading(false);
  };

  return (
    <Container>
      <Heading>Competitor Video Analysis</Heading>

      <Input
        type="text"
        placeholder="Enter Competitor's Channel Name"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <Button onClick={fetchVideos}>Analyze</Button>

      {loading && <p>Loading...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      {channelData && (
        <div>
          <h3>Channel: {channelData.title}</h3>
          <p>Subscribers: {channelData.subscriberCount}</p>
        </div>
      )}

      <h3>Latest Videos</h3>
      <List>
        {videos.map((video) => (
          <ListItem key={video.videoId}>
            <a href={`https://www.youtube.com/watch?v=${video.videoId}`} target="_blank" rel="noopener noreferrer">
              {video.title}
            </a>
            <p>Views: {video.views} | Likes: {video.likes} | Comments: {video.comments}</p>
          </ListItem>
        ))}
      </List>

      {keywordAnalysis && (
        <div>
          <h3>Keyword Analysis</h3>
          <p><strong>Top Keywords:</strong> {keywordAnalysis.keywords?.join(", ")}</p>
          <p><strong>Top Bigrams:</strong> {keywordAnalysis.bigrams?.join(", ")}</p>
          <p><strong>Top Trigrams:</strong> {keywordAnalysis.trigrams?.join(", ")}</p>

          {/* ✅ Wrapped TF-IDF Scores */}
          <h3>TF-IDF Scores</h3>
          <TfidfContainer>
            <pre>{JSON.stringify(keywordAnalysis.tfidfScores, null, 2)}</pre>
          </TfidfContainer>

          <h3>Sentiment Score</h3>
          <p>{keywordAnalysis.avgSentiment}</p>
        </div>
      )}

      {keywordAnalysis && (
        <div>
          <h3>Trending Keywords</h3>
          <List>
            {keywordAnalysis.trendingKeywords?.map((keyword, index) => (
              <ListItem key={index}>{keyword}</ListItem>
            ))}
          </List>

          <h3>Suggested Title</h3>
          <p>{keywordAnalysis.suggestedTitle}</p>

          <h3>Suggested Tags</h3>
          <List>
            {keywordAnalysis.suggestedTags?.map((tag, index) => (
              <ListItem key={index}>#{tag}</ListItem>
            ))}
          </List>
        </div>
      )}
    </Container>
  );
}

export default CompetitorAnalysis;
