import React, { useState, useEffect } from "react";
import axios from "axios";
import styled from "styled-components";
import { saveAs } from "file-saver";

const Container = styled.div`
  font-family: "San Francisco", Helvetica, "Myriad Set";
  background-color: #f5f5f7;
  color: #333;
  max-width: 900px;
  margin: 50px auto;
  padding: 20px;
  border-radius: 10px;
  box-shadow: 0px 10px 30px rgba(0, 0, 0, 0.1);
`;

const Button = styled.button`
  background-color: #007aff;
  color: white;
  border: none;
  padding: 10px 15px;
  margin-top: 10px;
  font-size: 16px;
  border-radius: 5px;
  cursor: pointer;
  &:hover {
    background-color: #005ecb;
  }
`;

const KeywordBox = styled.div`
  background: white;
  padding: 10px;
  margin: 10px 0;
  border-radius: 5px;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.1);
`;

function CompetitorAnalysis() {
  const [username, setUsername] = useState("");
  const [channelData, setChannelData] = useState(null);
  const [videos, setVideos] = useState([]);
  const [keywordAnalysis, setKeywordAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchKeywordAnalysis = async () => {
    try {
      const response = await axios.get("http://localhost:5000/videos");
      setKeywordAnalysis(response.data);
    } catch (err) {
      setError("Failed to fetch keyword analysis.");
    }
  };

  const fetchVideos = async () => {
    setLoading(true);
    setError(null);

    try {
      const videoResponse = await axios.post("http://localhost:5000/analyze", { username });
      if (videoResponse.data.error) {
        throw new Error(videoResponse.data.error);
      }

      setChannelData(videoResponse.data.channelData);
      setVideos(videoResponse.data.videos);
      await fetchKeywordAnalysis();
    } catch (err) {
      setError("Failed to fetch data. Check the username and API.");
    }

    setLoading(false);
  };

  useEffect(() => {
    fetchKeywordAnalysis();
  }, []);

  const exportData = (format) => {
    const data = keywordAnalysis;
    if (!data) return;

    if (format === "csv") {
      let csvContent = "data:text/csv;charset=utf-8,";
      csvContent += "Keyword,TF-IDF Score\n";
      Object.entries(data.tfidfScores).forEach(([key, value]) => {
        csvContent += `${key},${value}\n`;
      });
      const encodedUri = encodeURI(csvContent);
      const link = document.createElement("a");
      link.setAttribute("href", encodedUri);
      link.setAttribute("download", "keyword_analysis.csv");
      document.body.appendChild(link);
      link.click();
    } else if (format === "json") {
      const blob = new Blob([JSON.stringify(data, null, 2)], { type: "application/json" });
      saveAs(blob, "keyword_analysis.json");
    }
  };

  return (
    <Container>
      <h2>Competitor Video Analysis</h2>
      <input
        type="text"
        placeholder="Enter Competitor's Channel Name"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <Button onClick={fetchVideos}>Get Videos</Button>

      {loading && <p>Loading...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      {channelData && (
        <div>
          <h3>Channel: {channelData.title}</h3>
          <p>Subscribers: {channelData.subscriberCount}</p>
        </div>
      )}

      <ul>
        {videos.map((video) => (
          <KeywordBox key={video.videoId}>
            <a href={`https://www.youtube.com/watch?v=${video.videoId}`} target="_blank" rel="noopener noreferrer">
              {video.title}
            </a>
            <p>Views: {video.views} | Likes: {video.likes} | Comments: {video.comments}</p>
          </KeywordBox>
        ))}
      </ul>

      {keywordAnalysis && (
        <div>
          <h3>Keyword Analysis</h3>
          <KeywordBox>
            <p><strong>Top Keywords:</strong> {keywordAnalysis.keywords?.join(", ")}</p>
            <p><strong>Top Bigrams:</strong> {keywordAnalysis.bigrams?.join(", ")}</p>
            <p><strong>Top Trigrams:</strong> {keywordAnalysis.trigrams?.join(", ")}</p>
            <p><strong>TF-IDF Scores:</strong> <pre style={{ whiteSpace: "pre-wrap" }}>{JSON.stringify(keywordAnalysis.tfidfScores, null, 2)}</pre></p>
            <p><strong>Suggested Title:</strong> {keywordAnalysis.suggestedTitle}</p>
            <p><strong>Suggested Tags:</strong> {keywordAnalysis.suggestedTags?.join(" ")}</p>
          </KeywordBox>
          <Button onClick={() => exportData("csv")}>Export as CSV</Button>
          <Button onClick={() => exportData("json")}>Export as JSON</Button>
        </div>
      )}
    </Container>
  );
}

export default CompetitorAnalysis;
