import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import styled from "styled-components";

const Container = styled.div`
  font-family: "San Francisco", Helvetica, "Myriad Set";
  background-color: #f5f5f7;
  color: #333;
  max-width: 1200px;
  margin: 50px auto;
  padding: 20px;
  border-radius: 16px;
  box-shadow: 0px 15px 40px rgba(0, 0, 0, 0.1);
  text-align: center;
  border: 2px solid #007aff;
  position: relative;
`;

const Loading = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(255, 255, 255, 0.8);
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 24px;
  font-weight: bold;
  color: #007aff;
`;

const ProgressBar = styled.div`
  width: 100%;
  background: #e0e0e0;
  border-radius: 12px;
  overflow: hidden;
  margin: 10px 0;

  div {
    height: 45px;
    text-align: center;
    line-height: 45px;
    color: white;
    font-size: 18px;
    font-weight: bold;
    border-radius: 12px;
    transition: width 0.5s ease, background-color 0.3s ease, transform 0.3s ease;
    cursor: pointer;

    &:hover {
      background-color: #005bb5;
      transform: scale(1.05);
    }
  }
`;

function CompetitorAnalysis() {
  const [username, setUsername] = useState("");
  const [keywordAnalysis, setKeywordAnalysis] = useState(null);
  const [comparisonData, setComparisonData] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchKeywordAnalysis = useCallback(async () => {
    try {
      const response = await axios.get("http://localhost:5000/videos");
      setKeywordAnalysis(response.data);
    } catch (err) {
      console.error("Failed to fetch keyword analysis.");
    }
  }, []);

  const fetchComparison = useCallback(async () => {
    if (!username) return;
    setLoading(true);
    try {
      const response = await axios.post("http://localhost:5000/compare", { username });
      setComparisonData(response.data);
    } catch (err) {
      console.error("Comparison failed.");
    }
    setLoading(false);
  }, [username]);

  useEffect(() => {
    fetchKeywordAnalysis();
  }, [fetchKeywordAnalysis]);

  return (
    <Container>
      {loading && <Loading>Analyzing...</Loading>}

      <h1>Competitor Video Analysis Dashboard</h1>
      <input
        type="text"
        placeholder="Enter Competitor's Channel Name"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <button onClick={fetchComparison}>Compare</button>

      {comparisonData.map((data, index) => (
        <div key={index}>
          <h2>Comparison with: {data.channelTitle}</h2>
          <h3>Top Keywords</h3>
          {data.keywords?.map((keyword, i) => (
            <ProgressBar key={i}>
              <div style={{ width: `${Math.min((i + 1) * 10, 100)}%`, background: "#007aff" }}>{keyword}</div>
            </ProgressBar>
          ))}
        </div>
      ))}

      {keywordAnalysis && (
        <div>
          <h2>Keyword Analysis</h2>
          <h3>Top Keywords</h3>
          {keywordAnalysis.keywords?.map((keyword, index) => (
            <ProgressBar key={index}>
              <div style={{ width: `${Math.min((index + 1) * 10, 100)}%`, background: "#007aff" }}>{keyword}</div>
            </ProgressBar>
          ))}
        </div>
      )}
    </Container>
  );
}

export default CompetitorAnalysis;
