import React, { useState, useEffect } from "react";
import axios from "axios";
import styled from "styled-components";
import { BarChart, Bar, LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from "recharts";

const Container = styled.div`
  font-family: "San Francisco", Helvetica, "Myriad Set";
  background-color: #f5f5f7;
  color: #333;
  max-width: 1100px;
  margin: 50px auto;
  padding: 20px;
  border-radius: 12px;
  box-shadow: 0px 10px 30px rgba(0, 0, 0, 0.1);
  text-align: center;
  overflow-x: auto;
`;

const Input = styled.input`
  width: 80%;
  padding: 12px;
  margin: 15px 0;
  border: 1px solid #ccc;
  border-radius: 8px;
  font-size: 16px;
  outline: none;
`;

const Button = styled.button`
  background-color: #007aff;
  color: white;
  border: none;
  padding: 12px 20px;
  margin-top: 15px;
  font-size: 18px;
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.3s ease;
  &:hover {
    background-color: #005ecb;
    transform: scale(1.05);
  }
`;

const KeywordBox = styled.div`
  background: white;
  padding: 15px;
  margin: 15px 0;
  border-radius: 8px;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.1);
  text-align: left;
  overflow-x: auto;
`;

function CompetitorAnalysis() {
  const [username, setUsername] = useState("");
  const [channelData, setChannelData] = useState(null);
  const [videos, setVideos] = useState([]);
  const [keywordAnalysis, setKeywordAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchKeywordAnalysis = async () => {
    try {
      const response = await axios.get("http://localhost:5000/videos");
      setKeywordAnalysis(response.data);
    } catch (err) {
      setError("Failed to fetch keyword analysis.");
    }
  };

  const fetchVideos = async () => {
    setLoading(true);
    setError(null);

    try {
      const videoResponse = await axios.post("http://localhost:5000/analyze", { username });
      if (videoResponse.data.error) throw new Error(videoResponse.data.error);
      setChannelData(videoResponse.data.channelData);
      setVideos(videoResponse.data.videos);
      await fetchKeywordAnalysis();
    } catch (err) {
      setError("Failed to fetch data. Check the username and API.");
    }

    setLoading(false);
  };

  useEffect(() => {
    fetchKeywordAnalysis();
  }, []);

  return (
    <Container>
      <h2>Competitor Video Analysis</h2>
      <Input
        type="text"
        placeholder="Enter Competitor's Channel Name"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <Button onClick={fetchVideos}>Get Videos</Button>

      {loading && <p>Loading...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      {channelData && (
        <div>
          <h3>Channel: {channelData.title}</h3>
          <p>Subscribers: {channelData.subscriberCount}</p>
        </div>
      )}

      <ul>
        {videos.map((video) => (
          <KeywordBox key={video.videoId}>
            <a href={`https://www.youtube.com/watch?v=${video.videoId}`} target="_blank" rel="noopener noreferrer">
              {video.title}
            </a>
            <p>Views: {video.views} | Likes: {video.likes} | Comments: {video.comments}</p>
          </KeywordBox>
        ))}
      </ul>

      {keywordAnalysis && (
        <div>
          <h3>Keyword Analysis</h3>
          <KeywordBox>
            <h4>Keyword Frequency</h4>
            <BarChart width={600} height={300} data={keywordAnalysis.keywordFrequency}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="keyword" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="frequency" fill="#007aff" />
            </BarChart>

            <h4>Sentiment Analysis</h4>
            <LineChart width={600} height={300} data={keywordAnalysis.sentimentScores}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="video" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="score" stroke="#ff3b30" />
            </LineChart>

            <h3>TF-IDF Scores</h3>
            <ul style={{ whiteSpace: "pre-wrap", wordBreak: "break-word" }}>
              {Object.entries(keywordAnalysis.tfidfScores || {}).map(([word, score]) => (
                <li key={word}>{`${word}: ${score.toFixed(2)}`}</li>
              ))}
            </ul>

            <h3>Suggested Keywords for Your Next Video</h3>
            <ul>
              {keywordAnalysis.suggestedKeywords?.map((keyword, index) => (
                <li key={index}>{keyword}</li>
              ))}
            </ul>

            <p><strong>Suggested Title:</strong> {keywordAnalysis.suggestedTitle || "N/A"}</p>
            <p><strong>Suggested Tags:</strong> {keywordAnalysis.suggestedTags?.join(", ") || "N/A"}</p>
          </KeywordBox>
        </div>
      )}
    </Container>
  );
}

export default CompetitorAnalysis;
