import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import styled from "styled-components";

const Container = styled.div`
  font-family: "San Francisco", Helvetica, "Myriad Set";
  background-color: #f5f5f7;
  color: #333;
  max-width: 1100px;
  margin: 50px auto;
  padding: 20px;
  border-radius: 12px;
  box-shadow: 0px 10px 30px rgba(0, 0, 0, 0.1);
  text-align: center;
  transition: all 0.3s ease;
  border: 2px solid #007aff;
`;

const Input = styled.input`
  padding: 12px;
  border: 2px solid #007aff;
  border-radius: 10px;
  margin-right: 12px;
  font-size: 18px;
  width: 320px;
`;

const Button = styled.button`
  padding: 12px 24px;
  background-color: #007aff;
  color: white;
  border: none;
  border-radius: 10px;
  cursor: pointer;
  font-size: 18px;
  transition: background-color 0.3s;

  &:hover {
    background-color: #005bb5;
  }
`;

const List = styled.ul`
  list-style: none;
  padding: 0;

  li {
    margin: 12px 0;
    a {
      color: #007aff;
      text-decoration: none;
      font-weight: bold;
    }
    p {
      font-size: 16px;
      color: #555;
    }
  }
`;

const KeywordBox = styled.div`
  margin: 20px 0;
  padding: 15px;
  border: 1px solid #ccc;
  border-radius: 8px;
  background: #e6f2ff;
`;

function CompetitorAnalysis() {
  const [username, setUsername] = useState("");
  const [channelData, setChannelData] = useState(null);
  const [videos, setVideos] = useState([]);
  const [keywordAnalysis, setKeywordAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchKeywordAnalysis = useCallback(async () => {
    try {
      const response = await axios.get("http://localhost:5000/videos");
      setKeywordAnalysis(response.data);
    } catch (err) {
      setError("Failed to fetch keyword analysis.");
    }
  }, []);

  const fetchVideos = useCallback(async () => {
    setLoading(true);
    setError(null);

    try {
      const videoResponse = await axios.post("http://localhost:5000/analyze", { username });
      if (videoResponse.data.error) throw new Error(videoResponse.data.error);
      setChannelData(videoResponse.data.channelData);
      setVideos(videoResponse.data.videos);
      setKeywordAnalysis(videoResponse.data.keywordAnalysis);
    } catch (err) {
      setError("Failed to fetch data. Check the username and API.");
    }

    setLoading(false);
  }, [username]);

  useEffect(() => {
    fetchKeywordAnalysis();
  }, [fetchKeywordAnalysis]);

  return (
    <Container>
      <h2>Competitor Video Analysis</h2>
      <Input
        type="text"
        placeholder="Enter Competitor's Channel Name"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <Button onClick={fetchVideos}>Get Videos</Button>

      {loading && <p>Loading...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      {channelData && (
        <div>
          <h3>Channel: {channelData.title}</h3>
          <p>Subscribers: {channelData.subscriberCount}</p>
        </div>
      )}

      <List>
        {videos.map((video) => (
          <li key={video.videoId}>
            <a href={`https://www.youtube.com/watch?v=${video.videoId}`} target="_blank" rel="noopener noreferrer">
              {video.title}
            </a>
            <p>Views: {video.views} | Likes: {video.likes} | Comments: {video.comments}</p>
          </li>
        ))}
      </List>

      {keywordAnalysis && (
        <div>
          <h3>Keyword Analysis</h3>

          <KeywordBox>
            <h4>Top Keywords</h4>
            <p>{keywordAnalysis.keywords?.join(", ") || "N/A"}</p>
          </KeywordBox>

          <KeywordBox>
            <h4>Top Bigrams</h4>
            <p>{keywordAnalysis.bigrams?.join(", ") || "N/A"}</p>
          </KeywordBox>

          <KeywordBox>
            <h4>Top Trigrams</h4>
            <p>{keywordAnalysis.trigrams?.join(", ") || "N/A"}</p>
          </KeywordBox>

          <KeywordBox>
            <h4>TF-IDF Scores</h4>
            {Object.entries(keywordAnalysis.tfidfScores || {}).map(([word, score], index) => (
              <div key={index}>{`${word}: ${score.toFixed(2)}`}</div>
            ))}
          </KeywordBox>

          <KeywordBox>
            <h4>Suggested Keywords for Your Next Video</h4>
            <p>{keywordAnalysis.suggestedKeywords?.join(", ") || "N/A"}</p>
          </KeywordBox>

          <KeywordBox>
            <p><strong>Suggested Title:</strong> {keywordAnalysis.suggestedTitle || "N/A"}</p>
            <p><strong>Suggested Tags:</strong> {keywordAnalysis.suggestedTags?.join(", ") || "N/A"}</p>
          </KeywordBox>
        </div>
      )}
    </Container>
  );
}

export default CompetitorAnalysis;
