import React, { useState, useEffect } from "react";
import axios from "axios";
import styled from "styled-components";
import { Bar, Line, Bubble } from "react-chartjs-2";
import { Chart, registerables } from "chart.js";

Chart.register(...registerables);

const Container = styled.div`
  font-family: "San Francisco", Helvetica, "Myriad Set";
  background-color: #f5f5f7;
  color: #333;
  max-width: 900px;
  margin: 50px auto;
  padding: 20px;
  border-radius: 12px;
  box-shadow: 0px 10px 30px rgba(0, 0, 0, 0.1);
  text-align: center;
`;

const Input = styled.input`
  width: 80%;
  padding: 12px;
  margin: 15px 0;
  border: 1px solid #ccc;
  border-radius: 8px;
  font-size: 16px;
  outline: none;
`;

const Button = styled.button`
  background-color: #007aff;
  color: white;
  border: none;
  padding: 12px 20px;
  margin-top: 15px;
  font-size: 18px;
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.3s ease;
  &:hover {
    background-color: #005ecb;
    transform: scale(1.05);
  }
`;

const KeywordBox = styled.div`
  background: white;
  padding: 15px;
  margin: 15px 0;
  border-radius: 8px;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.1);
  text-align: left;
`;

function CompetitorAnalysis() {
  const [username, setUsername] = useState("");
  const [channelData, setChannelData] = useState(null);
  const [videos, setVideos] = useState([]);
  const [keywordAnalysis, setKeywordAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchKeywordAnalysis = async () => {
    try {
      const response = await axios.get("http://localhost:5500/videos");
      setKeywordAnalysis(response.data);
    } catch (err) {
      setError("Failed to fetch keyword analysis.");
    }
  };

  const fetchVideos = async () => {
    setLoading(true);
    setError(null);

    try {
      const videoResponse = await axios.post("http://localhost:5500/analyze", { username });
      if (videoResponse.data.error) {
        throw new Error(videoResponse.data.error);
      }

      setChannelData(videoResponse.data.channelData);
      setVideos(videoResponse.data.videos);
      await fetchKeywordAnalysis();
    } catch (err) {
      setError("Failed to fetch data. Check the username and API.");
    }

    setLoading(false);
  };

  useEffect(() => {
    fetchKeywordAnalysis();
  }, []);

  return (
    <Container>
      <h2>Competitor Video Analysis</h2>
      <Input
        type="text"
        placeholder="Enter Competitor's Channel Name"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <Button onClick={fetchVideos}>Get Videos</Button>

      {loading && <p>Loading...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      {channelData && (
        <div>
          <h3>Channel: {channelData.title}</h3>
          <p>Subscribers: {channelData.subscriberCount}</p>
        </div>
      )}

      <ul>
        {videos.map((video) => (
          <KeywordBox key={video.videoId}>
            <a href={`https://www.youtube.com/watch?v=${video.videoId}`} target="_blank" rel="noopener noreferrer">
              {video.title}
            </a>
            <p>Views: {video.views} | Likes: {video.likes} | Comments: {video.comments}</p>
          </KeywordBox>
        ))}
      </ul>

      {keywordAnalysis && (
        <div>
          <h3>Keyword Analysis</h3>
          <KeywordBox>
            <p><strong>Top Keywords:</strong> {keywordAnalysis.keywords?.join(", ")}</p>
            <p><strong>Top Bigrams:</strong> {keywordAnalysis.bigrams?.join(", ")}</p>
            <p><strong>Top Trigrams:</strong> {keywordAnalysis.trigrams?.join(", ")}</p>
            <h3>Keyword Frequency</h3>
            <Bar
              data={{
                labels: keywordAnalysis.keywords,
                datasets: [{
                  label: "Keyword Frequency",
                  data: keywordAnalysis.keywords.map(() => Math.floor(Math.random() * 100)),
                  backgroundColor: "#007aff"
                }]
              }}
            />
            <h3>Sentiment Analysis Over Time</h3>
            <Line
              data={{
                labels: keywordAnalysis.keywords,
                datasets: [{
                  label: "Sentiment Score",
                  data: keywordAnalysis.keywords.map(() => Math.random() * 2 - 1),
                  borderColor: "#ff3b30",
                  fill: false
                }]
              }}
            />
            <h3>TF-IDF Scores</h3>
            <Bubble
              data={{
                datasets: [{
                  label: "TF-IDF Scores",
                  data: keywordAnalysis.keywords.map(() => ({ x: Math.random() * 10, y: Math.random() * 10, r: Math.random() * 10 })),
                  backgroundColor: "#34c759"
                }]
              }}
            />
            <p><strong>Suggested Title:</strong> {keywordAnalysis.suggestedTitle}</p>
            <p><strong>Suggested Tags:</strong> {keywordAnalysis.suggestedTags?.join(" ")}</p>
          </KeywordBox>
        </div>
      )}
    </Container>
  );
}

export default CompetitorAnalysis;
